p.m1 = 1;p.m2 = 1;p.g = 1;p.I1 = 1;p.I2 = 1;p.l1 = 1; p.l2 = 1;p.lc1 = 0.5;p.lc2 = 0.5;solution = ode45(@(t,y)dynamics(y,square(t/(t/100+0.1)),p), [0,20], [-pi/2, 0, 0,0]);t = solution.x;u = square(t./(t./100+0.1));plot(t, solution.y)hold onplot(t,u)guess.time =t;guess.control = u;guess.state = solution.y;save guess.mat guess p%% animatefor i = 1:length(t)    clf; hold on;    animate(t(i), solution.y(:,i), p)    drawnow; pause(0.01); end